mapper.select:
select * from ( select Tb.*, rownum rNum FROM( select * from (select st.*, store_tag from stores st , tag t where st.STORE_IDX = t.STORE_IDX(+))
where store_localAddr LIKE '%'||#{searchTxt}||'%' or store_name LIKE '%'||#{searchTxt}||'%' or store_item LIKE '%'||#{searchTxt}||'%' or store_tag LIKE '%'||#{searchTxt}||'%'
order by STORE_IDX desc )Tb ) WHERE rNum>=#{start} AND rNum<=#{end}

mapper.update:

UPDATE stores
	SET store_name=#{store_name}, store_owner=#{store_owner}, store_notice=#{store_notice}, store_phone=#{store_phone}, 
	store_localaddr=#{store_localaddr}, store_zipcode=#{store_zipcode}, 
	store_roadaddr=#{store_roadaddr}, store_latitude=#{store_latitude}, 
	store_longitude=#{store_longitude}, store_sigungu=#{store_sigungu}, store_dong=#{store_dong}
	WHERE mem_id=#{mem_id}

UPDATE stores
	SET store_menu=#{store_menu}
	WHERE mem_id=#{mem_id}

UPDATE tag
	SET store_tag=#{store_tag}
	WHERE store_idx=#{store_idx}

UPDATE image
	SET image_save=#{image_save}
	WHERE store_idx=#{store_idx}
